	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(MatriculesPeer::DATABASE_NAME);

		if ($this->isColumnModified(MatriculesPeer::IDMATRICULES)) $criteria->add(MatriculesPeer::IDMATRICULES, $this->idmatricules);
		if ($this->isColumnModified(MatriculesPeer::USUARIS_USUARIID)) $criteria->add(MatriculesPeer::USUARIS_USUARIID, $this->usuaris_usuariid);
		if ($this->isColumnModified(MatriculesPeer::CURSOS_IDCURSOS)) $criteria->add(MatriculesPeer::CURSOS_IDCURSOS, $this->cursos_idcursos);
		if ($this->isColumnModified(MatriculesPeer::ESTAT)) $criteria->add(MatriculesPeer::ESTAT, $this->estat);
		if ($this->isColumnModified(MatriculesPeer::COMENTARI)) $criteria->add(MatriculesPeer::COMENTARI, $this->comentari);
		if ($this->isColumnModified(MatriculesPeer::DATAINSCRIPCIO)) $criteria->add(MatriculesPeer::DATAINSCRIPCIO, $this->datainscripcio);
		if ($this->isColumnModified(MatriculesPeer::PAGAT)) $criteria->add(MatriculesPeer::PAGAT, $this->pagat);
		if ($this->isColumnModified(MatriculesPeer::TREDUCCIO)) $criteria->add(MatriculesPeer::TREDUCCIO, $this->treduccio);
		if ($this->isColumnModified(MatriculesPeer::TPAGAMENT)) $criteria->add(MatriculesPeer::TPAGAMENT, $this->tpagament);
		if ($this->isColumnModified(MatriculesPeer::SITE_ID)) $criteria->add(MatriculesPeer::SITE_ID, $this->site_id);
		if ($this->isColumnModified(MatriculesPeer::ACTIU)) $criteria->add(MatriculesPeer::ACTIU, $this->actiu);
		if ($this->isColumnModified(MatriculesPeer::TPV_OPERACIO)) $criteria->add(MatriculesPeer::TPV_OPERACIO, $this->tpv_operacio);
		if ($this->isColumnModified(MatriculesPeer::TPV_ORDER)) $criteria->add(MatriculesPeer::TPV_ORDER, $this->tpv_order);

		return $criteria;
	}

