			$currNorm = ($this->dataacceptaciocondicions !== null && $tmpDt = new DateTime($this->dataacceptaciocondicions)) ? $tmpDt->format('Y-m-d H:i:s') : null;
			$newNorm = ($dt !== null) ? $dt->format('Y-m-d H:i:s') : null;

			if ( ($currNorm !== $newNorm) // normalized values don't match 
					)
			{
