	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinUsuaris(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		ReservaespaisPeer::addSelectColumns($criteria);
		$startcol = (ReservaespaisPeer::NUM_COLUMNS - ReservaespaisPeer::NUM_LAZY_LOAD_COLUMNS);
		UsuarisPeer::addSelectColumns($criteria);

		$criteria->addJoin(ReservaespaisPeer::USUARIS_USUARIID, UsuarisPeer::USUARIID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseReservaespaisPeer', $criteria, $con);
		}

		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = ReservaespaisPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = ReservaespaisPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {

				$cls = ReservaespaisPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				ReservaespaisPeer::addInstanceToPool($obj1, $key1);
			} // if $obj1 already loaded

			$key2 = UsuarisPeer::getPrimaryKeyHashFromRow($row, $startcol);
			if ($key2 !== null) {
				$obj2 = UsuarisPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$cls = UsuarisPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol);
					UsuarisPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 already loaded
				
				// Add the $obj1 (Reservaespais) to $obj2 (Usuaris)
				$obj2->addReservaespais($obj1);

			} // if joined row was not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


