	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseReservaespaisPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseReservaespaisPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
