		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Reservaespais object
		}

		if ($criteria->containsKey(ReservaespaisPeer::RESERVAESPAIID) && $criteria->keyContainsValue(ReservaespaisPeer::RESERVAESPAIID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.ReservaespaisPeer::RESERVAESPAIID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseReservaespaisPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseReservaespaisPeer', $values, $con, $pk);
