	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TracPeer::DATABASE_NAME);

		if ($this->isColumnModified(TracPeer::IDTRAC)) $criteria->add(TracPeer::IDTRAC, $this->idtrac);
		if ($this->isColumnModified(TracPeer::TITLE)) $criteria->add(TracPeer::TITLE, $this->title);
		if ($this->isColumnModified(TracPeer::DESCRIPTION)) $criteria->add(TracPeer::DESCRIPTION, $this->description);
		if ($this->isColumnModified(TracPeer::TYPE)) $criteria->add(TracPeer::TYPE, $this->type);
		if ($this->isColumnModified(TracPeer::SOLVED_VERSION)) $criteria->add(TracPeer::SOLVED_VERSION, $this->solved_version);
		if ($this->isColumnModified(TracPeer::IMPORTANCY)) $criteria->add(TracPeer::IMPORTANCY, $this->importancy);
		if ($this->isColumnModified(TracPeer::USUARI_ID)) $criteria->add(TracPeer::USUARI_ID, $this->usuari_id);
		if ($this->isColumnModified(TracPeer::SITE_ID)) $criteria->add(TracPeer::SITE_ID, $this->site_id);
		if ($this->isColumnModified(TracPeer::ACTIU)) $criteria->add(TracPeer::ACTIU, $this->actiu);
		if ($this->isColumnModified(TracPeer::DATE)) $criteria->add(TracPeer::DATE, $this->date);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
