	/**
	 * Method perform an INSERT on the database, given a Usuaris or Criteria object.
	 *
	 * @param      mixed $values Criteria or Usuaris object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUsuarisPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseUsuarisPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(UsuarisPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Usuaris object
		}

		if ($criteria->containsKey(UsuarisPeer::USUARIID) && $criteria->keyContainsValue(UsuarisPeer::USUARIID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.UsuarisPeer::USUARIID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUsuarisPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseUsuarisPeer', $values, $con, $pk);
    }

		return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a Usuaris or Criteria object.
	 *
	 * @param      mixed $values Criteria or Usuaris object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUsuarisPeer:doUpdate:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseUsuarisPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(UsuarisPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(UsuarisPeer::USUARIID);
			$selectCriteria->add(UsuarisPeer::USUARIID, $criteria->remove(UsuarisPeer::USUARIID), $comparison);

		} else { // $values is Usuaris object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseUsuarisPeer:doUpdate:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseUsuarisPeer', $values, $con, $ret);
    }

    return $ret;
	}

	/**
	 * Method to DELETE all rows from the usuaris table.
	 *
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll($con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(UsuarisPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += UsuarisPeer::doOnDeleteCascade(new Criteria(UsuarisPeer::DATABASE_NAME), $con);
			UsuarisPeer::doOnDeleteSetNull(new Criteria(UsuarisPeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(UsuarisPeer::TABLE_NAME, $con);
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			UsuarisPeer::clearInstancePool();
			UsuarisPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Method perform a DELETE on the database, given a Usuaris or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Usuaris object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(UsuarisPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Usuaris) { // it's a model object
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else { // it's a primary key, or an array of pks
			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(UsuarisPeer::USUARIID, (array) $values, Criteria::IN);
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += UsuarisPeer::doOnDeleteCascade($criteria, $con);
			UsuarisPeer::doOnDeleteSetNull($criteria, $con);
			
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			if ($values instanceof Criteria) {
				UsuarisPeer::clearInstancePool();
			} elseif ($values instanceof Usuaris) { // it's a model object
				UsuarisPeer::removeInstanceFromPool($values);
			} else { // it's a primary key, or an array of pks
				foreach ((array) $values as $singleval) {
					UsuarisPeer::removeInstanceFromPool($singleval);
				}
			}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);
			UsuarisPeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = UsuarisPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related AppDocumentsPermisos objects
			$criteria = new Criteria(AppDocumentsPermisosPeer::DATABASE_NAME);
			
			$criteria->add(AppDocumentsPermisosPeer::IDUSUARI, $obj->getUsuariid());
			$affectedRows += AppDocumentsPermisosPeer::doDelete($criteria, $con);

			// delete related AppDocumentsPermisosDir objects
			$criteria = new Criteria(AppDocumentsPermisosDirPeer::DATABASE_NAME);
			
			$criteria->add(AppDocumentsPermisosDirPeer::IDUSUARI, $obj->getUsuariid());
			$affectedRows += AppDocumentsPermisosDirPeer::doDelete($criteria, $con);

			// delete related Cessio objects
			$criteria = new Criteria(CessioPeer::DATABASE_NAME);
			
			$criteria->add(CessioPeer::USUARI_ID, $obj->getUsuariid());
			$affectedRows += CessioPeer::doDelete($criteria, $con);

			// delete related Factures objects
			$criteria = new Criteria(FacturesPeer::DATABASE_NAME);
			
			$criteria->add(FacturesPeer::VALIDAUSUARI, $obj->getUsuariid());
			$affectedRows += FacturesPeer::doDelete($criteria, $con);

			// delete related Matricules objects
			$criteria = new Criteria(MatriculesPeer::DATABASE_NAME);
			
			$criteria->add(MatriculesPeer::USUARIS_USUARIID, $obj->getUsuariid());
			$affectedRows += MatriculesPeer::doDelete($criteria, $con);

			// delete related Missatges objects
			$criteria = new Criteria(MissatgesPeer::DATABASE_NAME);
			
			$criteria->add(MissatgesPeer::USUARIS_USUARIID, $obj->getUsuariid());
			$affectedRows += MissatgesPeer::doDelete($criteria, $con);

			// delete related Personal objects
			$criteria = new Criteria(PersonalPeer::DATABASE_NAME);
			
			$criteria->add(PersonalPeer::IDUSUARI, $obj->getUsuariid());
			$affectedRows += PersonalPeer::doDelete($criteria, $con);

			// delete related Reservaespais objects
			$criteria = new Criteria(ReservaespaisPeer::DATABASE_NAME);
			
			$criteria->add(ReservaespaisPeer::USUARIS_USUARIID, $obj->getUsuariid());
			$affectedRows += ReservaespaisPeer::doDelete($criteria, $con);

			// delete related UsuarisApps objects
			$criteria = new Criteria(UsuarisAppsPeer::DATABASE_NAME);
			
			$criteria->add(UsuarisAppsPeer::USUARI_ID, $obj->getUsuariid());
			$affectedRows += UsuarisAppsPeer::doDelete($criteria, $con);

			// delete related UsuarisMenus objects
			$criteria = new Criteria(UsuarisMenusPeer::DATABASE_NAME);
			
			$criteria->add(UsuarisMenusPeer::USUARI_ID, $obj->getUsuariid());
			$affectedRows += UsuarisMenusPeer::doDelete($criteria, $con);

			// delete related UsuarisSites objects
			$criteria = new Criteria(UsuarisSitesPeer::DATABASE_NAME);
			
			$criteria->add(UsuarisSitesPeer::USUARI_ID, $obj->getUsuariid());
			$affectedRows += UsuarisSitesPeer::doDelete($criteria, $con);

			// delete related Usuarisllistes objects
			$criteria = new Criteria(UsuarisllistesPeer::DATABASE_NAME);
			
			$criteria->add(UsuarisllistesPeer::USUARIS_USUARISID, $obj->getUsuariid());
			$affectedRows += UsuarisllistesPeer::doDelete($criteria, $con);
		}
		return $affectedRows;
	}

	/**
	 * This is a method for emulating ON DELETE SET NULL DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     void
	 */
	protected static function doOnDeleteSetNull(Criteria $criteria, PropelPDO $con)
	{

		// first find the objects that are implicated by the $criteria
		$objects = UsuarisPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {

			// set fkey col in related Log rows to NULL
			$selectCriteria = new Criteria(UsuarisPeer::DATABASE_NAME);
			$updateValues = new Criteria(UsuarisPeer::DATABASE_NAME);
			$selectCriteria->add(LogPeer::USUARIID, $obj->getUsuariid());
			$updateValues->add(LogPeer::USUARIID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

			// set fkey col in related Personal rows to NULL
			$selectCriteria = new Criteria(UsuarisPeer::DATABASE_NAME);
			$updateValues = new Criteria(UsuarisPeer::DATABASE_NAME);
			$selectCriteria->add(PersonalPeer::USUARIUPDATEID, $obj->getUsuariid());
			$updateValues->add(PersonalPeer::USUARIUPDATEID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

		}
	}

	/**
	 * Validates all modified columns of given Usuaris object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      Usuaris $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(Usuaris $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(UsuarisPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(UsuarisPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		return BasePeer::doValidate(UsuarisPeer::DATABASE_NAME, UsuarisPeer::TABLE_NAME, $columns);
	}

	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     Usuaris
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = UsuarisPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(UsuarisPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(UsuarisPeer::DATABASE_NAME);
		$criteria->add(UsuarisPeer::USUARIID, $pk);

		$v = UsuarisPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
	 * Retrieve multiple objects by pkey.
	 *
	 * @param      array $pks List of primary keys
	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(UsuarisPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(UsuarisPeer::DATABASE_NAME);
			$criteria->add(UsuarisPeer::USUARIID, $pks, Criteria::IN);
			$objs = UsuarisPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

